var cache = [];
var playback = false;

module.exports = {
    record: () => {
        playback = false;
        cache = []
    },

    play: () => {
        playback = true
    },

    add: (id) => {
        if(playback) {
            playback = false;
            cache = []
        }
        cache.push(id)
    },

    get: () => {
        if(!playback) return '';
        return cache.shift() || ''
    }
}
